import React, { useState, ChangeEvent, FormEvent } from 'react';
import TopBar from '../components/TopBar.tsx';

// Placeholder list of 7 places — replace with your actual company names
const placesList = [
  'Place 1',
  'Place 2',
  'Place 3',
  'Place 4',
  'Place 5',
  'Place 6',
  'Place 7',
];

const Reast: React.FC = () => {
  const [resume, setResume] = useState<File | null>(null);
  const [ranks, setRanks] = useState<number[]>(Array(placesList.length).fill(0));
  const [paragraph, setParagraph] = useState<string>('');

  const handleResumeChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setResume(e.target.files[0]);
    }
  };

  const handleRankChange = (index: number) => (e: ChangeEvent<HTMLSelectElement>) => {
    const newRanks = [...ranks];
    newRanks[index] = parseInt(e.target.value, 10);
    setRanks(newRanks);
  };

  const handleParagraphChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
    setParagraph(e.target.value);
  };

  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();
    const formData = new FormData();
    if (resume) formData.append('resume', resume);
    ranks.forEach((rank, idx) => {
      formData.append(`rank_${idx + 1}`, rank.toString());
    });
    formData.append('paragraph', paragraph);

    // TODO: send formData to your backend endpoint
    console.log('Submitting form:', { resume, ranks, paragraph });
  };

  return (
    <>
    <topBar/>
    <form onSubmit={handleSubmit} className="max-w-xl mx-auto p-6 bg-white rounded-lg shadow-md space-y-6">
      <div>
        <label htmlFor="resume" className="block text-sm font-medium text-gray-700">
          Upload Resume
        </label>
        <input
          id="resume"
          type="file"
          accept=".pdf,.doc,.docx"
          onChange={handleResumeChange}
          className="mt-1 block w-full text-sm text-gray-900"
        />
      </div>

      <div>
        <h2 className="text-lg font-medium text-gray-900">Rank Your Top Places to Work</h2>
        <div className="mt-2 space-y-4">
          {placesList.map((place, idx) => (
            <div key={idx} className="flex items-center space-x-4">
              <span className="w-32 text-gray-700">{place}</span>
              <select
                value={ranks[idx]}
                onChange={handleRankChange(idx)}
                className="mt-1 block pl-3 pr-10 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              >
                <option value={0}>Select rank</option>
                {Array.from({ length: placesList.length }, (_, i) => i + 1).map((num) => (
                  <option key={num} value={num}>{num}</option>
                ))}
              </select>
            </div>
          ))}
        </div>
      </div>

      <div>
        <label htmlFor="paragraph" className="block text-sm font-medium text-gray-700">
          Short Paragraph
        </label>
        <textarea
          id="paragraph"
          rows={4}
          value={paragraph}
          onChange={handleParagraphChange}
          className="mt-1 block w-full shadow-sm border border-gray-300 rounded-md p-2 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
        />
      </div>

      <div>
        <button
          type="submit"
          className="w-full py-2 px-4 bg-indigo-600 text-white font-medium rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500">
          Submit
        </button>
      </div>
    </form>
    </>
  );
};

export default Reast;
