import React, { useState } from 'react';
import ResumeUpload from './ResumeUpload';

interface RankFormProps {
    placesList: string[];
}

const RankForm: React.FC<RankFormProps> = ({ placesList }) => {
    const [ranks, setRanks] = useState<number[]>(Array(placesList.length).fill(0));
    const [paragraph, setParagraph] = useState('');
    const [resume, setResume] = useState<File | null>(null);

    const handleRankChange = (index: number) => (event: React.ChangeEvent<HTMLSelectElement>) => {
        const newRanks = [...ranks];
        newRanks[index] = parseInt(event.target.value, 10);
        setRanks(newRanks);
    };

    const handleParagraphChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setParagraph(event.target.value);
    };


    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        // Handle form submission logic here
        console.log({ ranks, paragraph, resume });
    };

    return (
        <form onSubmit={handleSubmit} className="max-w-xl mx-auto p-6 bg-white rounded-lg shadow-md space-y-6">
            <ResumeUpload/>
            <div>
                <h2 className="text-lg font-medium text-gray-900">Rank Your Top Places to Work</h2>
                <div className="mt-2 space-y-4">
                    {placesList.map((place, idx) => (
                        <div key={idx} className="flex items-center space-x-4">
                            <span className="w-32 text-gray-700">{place}</span>
                            <select
                                value={ranks[idx]}
                                onChange={handleRankChange(idx)}
                                className="mt-1 block pl-3 pr-10 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                            >
                                <option value={0}>Select rank</option>
                                {Array.from({ length: placesList.length }, (_, i) => i + 1).map((num) => (
                                    <option key={num} value={num}>{num}</option>
                                ))}
                            </select>
                        </div>
                    ))}
                </div>
            </div>

            <div>
                <label htmlFor="paragraph" className="block text-sm font-medium text-gray-700">
                    Short Paragraph
                </label>
                <textarea
                    id="paragraph"
                    rows={4}
                    value={paragraph}
                    onChange={handleParagraphChange}
                    className="mt-1 block w-full shadow-sm border border-gray-300 rounded-md p-2 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                />
            </div>

            <div>
                <button
                    type="submit"
                    className="w-full py-2 px-4 bg-indigo-600 text-white font-medium rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500">
                    Submit
                </button>
            </div>
        </form>
    );
};

export default RankForm;
