import React, { useState, FormEvent } from "react";
import welcomeImg from "../assets/img/Welcome_aboard.png";

const LoginHero: React.FC = () => {
  const [identifier, setIdentifier] = useState("");

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // TODO: replace with your actual submit logic
    console.log("Logging in as:", identifier);
  };

  return (
    <div className="w-full min-h-screen bg-myrtle-green flex items-center justify-center py-28 px-4">
      <div className="flex flex-col md:flex-row bg-white rounded-lg overflow-hidden shadow-lg max-w-7xl w-full">
        {/* Left side */}
        <div className="md:w-1/2 p-12">
          <h1 className="text-4xl md:text-6xl font-bold text-myrtle-green mb-4">
            Welcome Back! Please Log In
          </h1>
          <p className="text-gray-700 leading-7 mb-8">
            Access your account to connect with potential employers and find the perfect job.
          </p>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label htmlFor="identifier" className="sr-only">
                Username or Email
              </label>
              <input
                id="identifier"
                name="identifier"
                type="text"
                value={identifier}
                onChange={(e) => setIdentifier(e.target.value)}
                placeholder="Username or Email"
                required
                className="w-full border-2 border-gray-300 rounded-lg p-3 text-base leading-7 
                           focus:outline-none focus:ring-2 focus:ring-myrtle-green"
              />
            </div>

            <button
              type="submit"
              className="w-full bg-myrtle-green text-white py-3 rounded-lg text-lg font-medium 
                         hover:bg-myrtle-dark transition-colors"
            >
              Login
            </button>
          </form>
        </div>

        {/* Right side image */}
        <div className="hidden md:block md:w-1/2">
          <img
            src={welcomeImg}
            alt="Welcome aboard"
            className="w-full h-full object-cover"
          />
        </div>
      </div>
    </div>
  );
};

export default LoginHero;
